mapper.select:
SELECT


,address.yonghu_id as addressYonghuId
,address.address_name as addressName
,address.address_phone as addressPhone
,address.address_dizhi as addressDizhi
,address.isdefault_types as isdefaultTypes
,nongchanpin.shangjia_id as nongchanpinShangjiaId
,nongchanpin.nongchanpin_name as nongchanpinName
,nongchanpin.nongchanpin_uuid_number as nongchanpinUuidNumber
,nongchanpin.nongchanpin_photo as nongchanpinPhoto
,nongchanpin.nongchanpin_types as nongchanpinTypes
,nongchanpin.nongchanpin_kucun_number as nongchanpinKucunNumber
,nongchanpin.nongchanpin_old_money as nongchanpinOldMoney
,nongchanpin.nongchanpin_new_money as nongchanpinNewMoney
,nongchanpin.nongchanpin_price as nongchanpinPrice
,nongchanpin.nongchanpin_pintuan as nongchanpinPintuan
,nongchanpin.nongchanpin_clicknum as nongchanpinClicknum
,nongchanpin.zan_number as zanNumber
,nongchanpin.cai_number as caiNumber
,nongchanpin.nongchanpin_content as nongchanpinContent
,nongchanpin.shangxia_types as shangxiaTypes
,nongchanpin.nongchanpin_delete as nongchanpinDelete
,yonghu.yonghu_name as yonghuName
,yonghu.yonghu_phone as yonghuPhone
,yonghu.yonghu_id_number as yonghuIdNumber
,yonghu.yonghu_photo as yonghuPhoto
,yonghu.yonghu_email as yonghuEmail
,yonghu.new_money as newMoney
,yonghu.yonghu_sum_jifen as yonghuSumJifen
,yonghu.yonghu_new_jifen as yonghuNewJifen
,yonghu.huiyuandengji_types as huiyuandengjiTypes

FROM nongchanpin_order  a
left JOIN address address ON a.address_id = address.id
left JOIN nongchanpin nongchanpin ON a.nongchanpin_id = nongchanpin.id
left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



        and a.id in

        #{item}



        and a.nongchanpin_order_uuid_number like CONCAT('%',#{params.nongchanpinOrderUuidNumber},'%')


        and (
            a.address_id = #{params.addressId}
        )


        and (
            a.nongchanpin_id = #{params.nongchanpinId}
        )


        and (
            a.yonghu_id = #{params.yonghuId}
        )


          and a.buy_number >= #{params.buyNumberStart}   


          and a.buy_number <= #{params.buyNumberEnd}   


        and a.buy_number = #{params.buyNumber}


          and UNIX_TIMESTAMP(a.jiezhi_time) >= UNIX_TIMESTAMP(#{params.jiezhiTimeStart}) 


          and UNIX_TIMESTAMP(a.jiezhi_time) <= UNIX_TIMESTAMP(#{params.jiezhiTimeEnd}) 


          and a.nongchanpin_order_true_price >= #{params.nongchanpinOrderTruePriceStart}   


          and a.nongchanpin_order_true_price <= #{params.nongchanpinOrderTruePriceEnd}   


        and a.nongchanpin_order_courier_name like CONCAT('%',#{params.nongchanpinOrderCourierName},'%')


        and a.nongchanpin_order_courier_number like CONCAT('%',#{params.nongchanpinOrderCourierNumber},'%')


        and a.nongchanpin_order_types = #{params.nongchanpinOrderTypes}


        and a.nongchanpin_order_payment_types = #{params.nongchanpinOrderPaymentTypes}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        and a.address_id IS NOT NULL


        and address.address_name like CONCAT('%',#{params.addressName},'%')


        and address.address_phone like CONCAT('%',#{params.addressPhone},'%')


        and address.address_dizhi like CONCAT('%',#{params.addressDizhi},'%')


        and address.isdefault_types = #{params.isdefaultTypes}



          and UNIX_TIMESTAMP(address.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(address.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


          and UNIX_TIMESTAMP(address.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


          and UNIX_TIMESTAMP(address.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 



        and a.nongchanpin_id IS NOT NULL


        and nongchanpin.shangjia_id = #{params.shangjiaId}


        and nongchanpin.nongchanpin_name like CONCAT('%',#{params.nongchanpinName},'%')


        and nongchanpin.nongchanpin_uuid_number like CONCAT('%',#{params.nongchanpinUuidNumber},'%')


        and nongchanpin.nongchanpin_types = #{params.nongchanpinTypes}



          and nongchanpin.nongchanpin_kucun_number >= #{params.nongchanpinKucunNumberStart}   


          and nongchanpin.nongchanpin_kucun_number <= #{params.nongchanpinKucunNumberEnd}   


        and nongchanpin.nongchanpin_kucun_number = #{params.nongchanpinKucunNumber}


          and nongchanpin.nongchanpin_old_money >= #{params.nongchanpinOldMoneyStart}   


          and nongchanpin.nongchanpin_old_money <= #{params.nongchanpinOldMoneyEnd}   


          and nongchanpin.nongchanpin_new_money >= #{params.nongchanpinNewMoneyStart}   


          and nongchanpin.nongchanpin_new_money <= #{params.nongchanpinNewMoneyEnd}   


          and nongchanpin.nongchanpin_price >= #{params.nongchanpinPriceStart}   


          and nongchanpin.nongchanpin_price <= #{params.nongchanpinPriceEnd}   


        and nongchanpin.nongchanpin_price = #{params.nongchanpinPrice}


          and nongchanpin.nongchanpin_pintuan >= #{params.nongchanpinPintuanStart}   


          and nongchanpin.nongchanpin_pintuan <= #{params.nongchanpinPintuanEnd}   


        and nongchanpin.nongchanpin_pintuan = #{params.nongchanpinPintuan}


          and nongchanpin.nongchanpin_clicknum >= #{params.nongchanpinClicknumStart}   


          and nongchanpin.nongchanpin_clicknum <= #{params.nongchanpinClicknumEnd}   


        and nongchanpin.nongchanpin_clicknum = #{params.nongchanpinClicknum}


          and nongchanpin.zan_number >= #{params.zanNumberStart}   


          and nongchanpin.zan_number <= #{params.zanNumberEnd}   


        and nongchanpin.zan_number = #{params.zanNumber}


          and nongchanpin.cai_number >= #{params.caiNumberStart}   


          and nongchanpin.cai_number <= #{params.caiNumberEnd}   


        and nongchanpin.cai_number = #{params.caiNumber}


        and nongchanpin.nongchanpin_content like CONCAT('%',#{params.nongchanpinContent},'%')


        and nongchanpin.shangxia_types = #{params.shangxiaTypes}



          and nongchanpin.nongchanpin_delete >= #{params.nongchanpinDeleteStart}   


          and nongchanpin.nongchanpin_delete <= #{params.nongchanpinDeleteEnd}   


        and nongchanpin.nongchanpin_delete = #{params.nongchanpinDelete}


          and UNIX_TIMESTAMP(nongchanpin.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(nongchanpin.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        and a.yonghu_id IS NOT NULL


        and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


        and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


        and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


        and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


          and yonghu.new_money >= #{params.newMoneyStart}   


          and yonghu.new_money <= #{params.newMoneyEnd}   


          and yonghu.yonghu_sum_jifen >= #{params.yonghuSumJifenStart}   


          and yonghu.yonghu_sum_jifen <= #{params.yonghuSumJifenEnd}   


          and yonghu.yonghu_new_jifen >= #{params.yonghuNewJifenStart}   


          and yonghu.yonghu_new_jifen <= #{params.yonghuNewJifenEnd}   


        and yonghu.huiyuandengji_types = #{params.huiyuandengjiTypes}



order by a.${params.sort} ${params.order}
